package com.example.newsapp;

import android.os.Bundle;
import android.view.View;
import android.widget.ProgressBar;
import android.widget.Toast;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import java.util.ArrayList;

import retrofit2.Call;
import retrofit2.Callback;
import retrofit2.Response;
import retrofit2.Retrofit;
import retrofit2.converter.gson.GsonConverterFactory;

public class MainActivity extends AppCompatActivity implements CategoryRVAdapter.CategorClickInterface{

    // 73a8895c49a945609027d5faad632def

    private RecyclerView newsRV,categoryRV;
    private ProgressBar loadingPB;
    private ArrayList<Articles> articlesArrayList;
    private ArrayList<CategoryRVModel> categoryRVModelArrayList;
    private CategoryRVAdapter categoryRVAdapter;
    private NewsRVAdapter newsRVAdapter;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        newsRV = findViewById(R.id.idRVNews);
        categoryRV = findViewById(R.id.idRVCategories);
        loadingPB = findViewById(R.id.idPBLoading);
        articlesArrayList = new ArrayList<>();
        categoryRVModelArrayList = new ArrayList<>();
        newsRVAdapter = new NewsRVAdapter(articlesArrayList, this);
        categoryRVAdapter = new CategoryRVAdapter(categoryRVModelArrayList,this,this::onCategoryClick);
        newsRV.setLayoutManager(new LinearLayoutManager(this));
        newsRV.setAdapter(newsRVAdapter);
        categoryRV.setAdapter(categoryRVAdapter);
        getCategories();
        getNews("All");
        newsRVAdapter.notifyDataSetChanged();
    }

    private void getCategories(){
        categoryRVModelArrayList.add(new CategoryRVModel("","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSjXOVwUJWOKW9Hhnk7Ox_vGqmJJZwNoLxP9g&usqp=CAU"));
        categoryRVModelArrayList.add(new CategoryRVModel("","https://assets.thehansindia.com/h-upload/2021/07/31/1092805-tech.webp"));
        categoryRVModelArrayList.add(new CategoryRVModel("","https://img.freepik.com/free-vector/youtube-thumbnail-science-research_23-2150228578.jpg?w=1060&t=st=1708272930~exp=1708273530~hmac=d1cf8d4c2a2911b200855abfdcb8e545de1ea6ea9b551dd7d56f73bc1c0faf6e"));
        categoryRVModelArrayList.add(new CategoryRVModel("Sport","https://www.shutterstock.com/image-photo/grand-sports-collage-soccer-basketball-hockey-1071589835"));
        categoryRVModelArrayList.add(new CategoryRVModel("","https://scontent.fjai3-1.fna.fbcdn.net/v/t39.30808-1/310621667_212933574403181_2740232191726636357_n.png?stp=dst-png_p120x120&_nc_cat=100&ccb=1-7&_nc_sid=4da83f&_nc_ohc=JTPmiM7P3AsAX94sv6k&_nc_oc=AQmGhwS2KHZwm3d_ZRymtShpNbofNyzIRWziYL9Gl8VMNKpoTLXyhKcRK_bdDmTHlA5GUOVdw_gVR39BZTvGCqsL&_nc_ht=scontent.fjai3-1.fna&oh=00_AfDXW6lwnyutgcKRPaJogOaGr7WcDQtTAB-ndSq8jm6I3g&oe=65D6D7B4"));
        categoryRVModelArrayList.add(new CategoryRVModel("Business","data:image/jpeg;base64,"));
        categoryRVModelArrayList.add(new CategoryRVModel("Entertainment","data:image/jpeg;base64,"));
        categoryRVModelArrayList.add(new CategoryRVModel("Health","data:image/jpeg;base64,"));
        categoryRVAdapter.notifyDataSetChanged();
    }

    private void getNews(String category){
        loadingPB.setVisibility(View.VISIBLE);
        articlesArrayList.clear();
        String categoryURL = "https://newsapi.org/v2/top-headlines?country=in&category=" + category +"&apikey=73a8895c49a945609027d5faad632def";
        String url = "https://newsapi.org/v2/top-headlines?country=in&excludeDomains=stackoverflow.com&sortBy=publishedAt&language=en&apikey=73a8895c49a945609027d5faad632def";
        String BASE_URL = "https://newsapi.org/";
        Retrofit retrofit = new Retrofit.Builder().baseUrl(BASE_URL).addConverterFactory(GsonConverterFactory.create()).build();
        RetrofitAPI retrofitAPI = retrofit.create(RetrofitAPI.class);
        Call<NewsModel> call;
        if(category.equals("All")){
            call = retrofitAPI.getAllNews(url);
        }else{
            call = retrofitAPI.getNewsByCategory(categoryURL);
        }

        call.enqueue(new Callback<NewsModel>() {
            @Override
            public void onResponse(Call<NewsModel> call, Response<NewsModel> response) {
                NewsModel newsModel = response.body();
                loadingPB.setVisibility(View.GONE);
                ArrayList<Articles> articles = newsModel.getArticles();
                for (int i=0 ;i<articles.size();i++ ){
                    articlesArrayList.add(new Articles(articles.get(i).getTitle(),articles.get(i).getDescription(),articles.get(i).getUrlToImage(),articles.get(i).getUrl(),articles.get(i).getContent()));

                }
                newsRVAdapter.notifyDataSetChanged();


            }

            @Override
            public void onFailure(Call<NewsModel> call, Throwable t) {
                Toast.makeText(MainActivity.this, "Fail to get news", Toast.LENGTH_SHORT).show();

            }
        });
    }

    @Override
    public void onCategoryClick(int position) {
        String category = categoryRVModelArrayList.get(position).getCatagory();
        getNews(category);

    }
}